<?phpnamespace App\Validator;use Symfony\Component\Validator\Constraint;use Symfony\Component\Validator\ConstraintValidator;class AntispamValidator extends ConstraintValidator{    public function validate($value, Constraint $constraint)    {        if (!preg_match('/^[a-zA-Z]+$/', $value, $matches)) {            $this->context->buildViolation($constraint->message)                ->setParameters(['%string%' => $value])                ->addViolation();        }    }}